#if (CC_PIPELINE_TYPE == 0 || CC_FORCE_FORWARD_SHADING)
  layout(location = 0) out vec4 fragColorX;
  void main()  {
  #if CC_DISABLE_STRUCTURE_IN_FRAGMENT_SHADER
    float NoL = dot(-cc_mainLitDir.xyz, FSInput_worldNormal.xyz);
