      vec3 rotationDir = RotationVecFromAxisY(R.xyz, cc_surfaceTransform.z, cc_surfaceTransform.w);
      vec4 envmap = fragTextureLod(cc_environment, rotationDir, roughness * (mipCount - 1.0));
      #if CC_USE_IBL == 2
        return unpackRGBE(envmap);
